import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-420 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-420.md","filePath":"vacode/2.2-420.md"}'),t={name:"vacode/2.2-420.md"},o=n('<h1 id="§-2-2-420-exemptions" tabindex="-1">§ 2.2-420 Exemptions. <a class="header-anchor" href="#§-2-2-420-exemptions" aria-label="Permalink to &quot;§ 2.2-420 Exemptions.&quot;">​</a></h1><p>The registration and reporting provisions of this article shall not apply to:</p><ol><li><p>The Governor, Lieutenant Governor, Attorney General, and their immediate staffs or the Governor&#39;s Secretaries and their immediate staffs, acting in an official capacity;</p></li><li><p>Members of the General Assembly and other legislative officials and legislative employees acting in an official capacity;</p></li><li><p>Local elected officials acting in an official capacity;</p></li><li><p>Any employee of a state executive agency acting in an official capacity;</p></li><li><p>A duly elected or appointed official or employee of the United States acting in an official capacity;</p></li><li><p>An individual who limits lobbying solely to (i) formal testimony before a public meeting of an executive agency or legislative body and registers the appearance in the records of the agency or body and (ii) testimony and information compelled by action of an executive agency or legislative body;</p></li><li><p>A person who receives $500 or less in compensation and reimbursements, excluding personal living and travel expenses that are not reimbursed from any other source, in a calendar year for his lobbying activities;</p></li><li><p>A person who receives no compensation or anything of value for lobbying, and does not expend more than $500, excluding personal living and travel expenses that are not reimbursed from any other source, in lobbying in the calendar year; or</p></li><li><p>An employee of a business, other entity, or local government whose job duties do not regularly include lobbying.</p></li></ol><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0857" target="_blank" rel="noreferrer">857</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0937" target="_blank" rel="noreferrer">937</a>, § 2.1-781; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0843" target="_blank" rel="noreferrer">843</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0763" target="_blank" rel="noreferrer">763</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>.</p>',4),r=[o];function l(s,c,p,f,g,d){return i(),a("div",null,r)}const v=e(t,[["render",l]]);export{m as __pageData,v as default};
