import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-421 Reporting requirements for certain state agencies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-421.md","filePath":"vacode/2.2-421.md"}'),r={name:"vacode/2.2-421.md"},o=a('<h1 id="§-2-2-421-reporting-requirements-for-certain-state-agencies" tabindex="-1">§ 2.2-421 Reporting requirements for certain state agencies. <a class="header-anchor" href="#§-2-2-421-reporting-requirements-for-certain-state-agencies" aria-label="Permalink to &quot;§ 2.2-421 Reporting requirements for certain state agencies.&quot;">​</a></h1><p>A. The chief administrative officer of each board, department, institution, or agency of the Commonwealth shall file a registration statement with the Secretary of the Commonwealth on behalf of the officers and employees who will be engaged in lobbying as defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-419/" target="_blank" rel="noreferrer">2.2-419</a> and shall comply with the provisions of this article that require lobbyists to register with the Secretary of the Commonwealth. No fee shall be collected for registrations required by this section.</p><p>B. Any state governmental body required to file a registration under this section shall comply with the provisions of this article relating to registration.</p><p>C. The registration requirements of this section shall not apply to:</p><ol><li><p>The Governor, Lieutenant Governor, Attorney General and their immediate staffs, or the Governor&#39;s Secretaries and their deputies and immediate staffs, acting in an official capacity;</p></li><li><p>Members of the General Assembly and other legislative officials and legislative employees acting in an official capacity;</p></li><li><p>The chief administrative officer of each department or division in the executive branch of state government;</p></li><li><p>The chief administrative officer of each division of the State Corporation Commission; or</p></li><li><p>Any state government employee acting in an official capacity.</p></li></ol><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0915" target="_blank" rel="noreferrer">915</a>, § 2.1-781.1; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',6),n=[o];function s(l,c,f,h,p,d){return t(),i("div",null,n)}const _=e(r,[["render",s]]);export{g as __pageData,_ as default};
