import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-4311 Employment discrimination by contractor prohibited; required contract provisions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4311.md","filePath":"vacode/2.2-4311.md"}'),r={name:"vacode/2.2-4311.md"},a=i('<h1 id="§-2-2-4311-employment-discrimination-by-contractor-prohibited-required-contract-provisions" tabindex="-1">§ 2.2-4311 Employment discrimination by contractor prohibited; required contract provisions. <a class="header-anchor" href="#§-2-2-4311-employment-discrimination-by-contractor-prohibited-required-contract-provisions" aria-label="Permalink to &quot;§ 2.2-4311 Employment discrimination by contractor prohibited; required contract provisions.&quot;">​</a></h1><p>All public bodies shall include in every contract of more than $10,000 the following provisions:</p><ol><li>During the performance of this contract, the contractor agrees as follows:</li></ol><p>a. The contractor will not discriminate against any employee or applicant for employment because of race, religion, color, sex, national origin, age, disability, or other basis prohibited by state law relating to discrimination in employment, except where there is a bona fide occupational qualification reasonably necessary to the normal operation of the contractor. The contractor agrees to post in conspicuous places, available to employees and applicants for employment, notices setting forth the provisions of this nondiscrimination clause.</p><p>b. The contractor, in all solicitations or advertisements for employees placed by or on behalf of the contractor, will state that such contractor is an equal opportunity employer.</p><p>c. Notices, advertisements and solicitations placed in accordance with federal law, rule or regulation shall be deemed sufficient for the purpose of meeting the requirements of this section.</p><ol start="2"><li>The contractor will include the provisions of the foregoing paragraphs a, b and c in every subcontract or purchase order of over $10,000, so that the provisions will be binding upon each subcontractor or vendor.</li></ol><p>1982, c. 647, § 11-51; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0628" target="_blank" rel="noreferrer">628</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',8),n=[a];function c(s,l,p,d,h,m){return o(),t("div",null,n)}const b=e(r,[["render",c]]);export{u as __pageData,b as default};
