import{_ as t,o as r,c as a,z as e,a as o}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 2.2-4312 Drug-free workplace to be maintained by contractor; required contract provisions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4312.md","filePath":"vacode/2.2-4312.md"}'),n={name:"vacode/2.2-4312.md"},i=e("h1",{id:"§-2-2-4312-drug-free-workplace-to-be-maintained-by-contractor-required-contract-provisions",tabindex:"-1"},[o("§ 2.2-4312 Drug-free workplace to be maintained by contractor; required contract provisions. "),e("a",{class:"header-anchor",href:"#§-2-2-4312-drug-free-workplace-to-be-maintained-by-contractor-required-contract-provisions","aria-label":'Permalink to "§ 2.2-4312 Drug-free workplace to be maintained by contractor; required contract provisions."'},"​")],-1),c=e("p",null,"All public bodies shall include in every contract over $10,000 the following provisions:",-1),s=e("p",null,"During the performance of this contract, the contractor agrees to (i) provide a drug-free workplace for the contractor's employees; (ii) post in conspicuous places, available to employees and applicants for employment, a statement notifying employees that the unlawful manufacture, sale, distribution, dispensation, possession, or use of a controlled substance or marijuana is prohibited in the contractor's workplace and specifying the actions that will be taken against employees for violations of such prohibition; (iii) state in all solicitations or advertisements for employees placed by or on behalf of the contractor that the contractor maintains a drug-free workplace; and (iv) include the provisions of the foregoing clauses in every subcontract or purchase order of over $10,000, so that the provisions will be binding upon each subcontractor or vendor.",-1),l=e("p",null,'For the purposes of this section, "drug-free workplace" means a site for the performance of work done in connection with a specific contract awarded to a contractor in accordance with this chapter, the employees of whom are prohibited from engaging in the unlawful manufacture, sale, distribution, dispensation, possession or use of any controlled substance or marijuana during the performance of the contract.',-1),p=e("p",null,[o("2000, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0417",target:"_blank",rel:"noreferrer"},"417"),o(", § 11-51.1; 2001, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844",target:"_blank",rel:"noreferrer"},"844"),o(".")],-1),d=[i,c,s,l,p];function h(u,f,m,b,g,v){return r(),a("div",null,d)}const y=t(n,[["render",h]]);export{w as __pageData,y as default};
