import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-4321.2 Public works contracts; project labor agreements authorized.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4321.2.md","filePath":"vacode/2.2-4321.2.md"}'),a={name:"vacode/2.2-4321.2.md"},i=t('<h1 id="§-2-2-4321-2-public-works-contracts-project-labor-agreements-authorized" tabindex="-1">§ 2.2-4321.2 Public works contracts; project labor agreements authorized. <a class="header-anchor" href="#§-2-2-4321-2-public-works-contracts-project-labor-agreements-authorized" aria-label="Permalink to &quot;§ 2.2-4321.2 Public works contracts; project labor agreements authorized.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Project labor agreement&quot; means a pre-hire collective bargaining agreement with one or more labor organizations that establishes the terms and conditions of employment for a specific public works project.</p><p>&quot;Public body&quot; has the same meaning as provided in § <a href="/vacode/2.2-4301/">2.2-4301</a>.</p><p>&quot;Public works&quot; means the operation, erection, construction, alteration, improvement, maintenance, or repair of any public facility or immovable property owned, used, or leased by a public body.</p><p>B. Each public body, when engaged in procuring products or services or letting contracts for construction, manufacture, maintenance, or operation of public works, or when overseeing or administering such procurement, construction, manufacture, maintenance, or operation, may, in its bid specifications, project agreements, or other controlling documents:</p><ol><li><p>Require bidders, offerors, contractors, or subcontractors to enter into or adhere to project labor agreements with one or more labor organizations, on the same or related public works projects; and</p></li><li><p>Require bidders, offerors, contractors, subcontractors, or operators to become or remain signatories or otherwise to adhere to project labor agreements with one or more labor organizations, on the same or other related public works projects.</p></li></ol><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0685" target="_blank" rel="noreferrer">685</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0732" target="_blank" rel="noreferrer">732</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1203" target="_blank" rel="noreferrer">1203</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1251" target="_blank" rel="noreferrer">1251</a>.</p>',8),n=[i];function c(s,l,p,u,d,b){return r(),o("div",null,n)}const g=e(a,[["render",c]]);export{m as __pageData,g as default};
