import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-4328 Preference for local products and firms; applicability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4328.md","filePath":"vacode/2.2-4328.md"}'),o={name:"vacode/2.2-4328.md"},t=i('<h1 id="§-2-2-4328-preference-for-local-products-and-firms-applicability" tabindex="-1">§ 2.2-4328 Preference for local products and firms; applicability. <a class="header-anchor" href="#§-2-2-4328-preference-for-local-products-and-firms-applicability" aria-label="Permalink to &quot;§ 2.2-4328 Preference for local products and firms; applicability.&quot;">​</a></h1><p>A. The governing body of a county, city or town may, in the case of a tie bid, give preference to goods, services and construction produced in such locality or provided by persons, firms or corporations having principal places of business in the locality, if such a choice is available; otherwise the tie shall be decided by lot, unless § <a href="http://law.lis.virginia.gov/vacode/2.2-4324/" target="_blank" rel="noreferrer">2.2-4324</a> applies.</p><p>B. The provisions of this section shall apply only to bids submitted pursuant to a written Invitation to Bid.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0882" target="_blank" rel="noreferrer">882</a>, § 11-47.4; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',4),c=[t];function n(s,l,p,d,f,_){return a(),r("div",null,c)}const b=e(o,[["render",n]]);export{u as __pageData,b as default};
