import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-4331 Contract pricing arrangements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4331.md","filePath":"vacode/2.2-4331.md"}'),i={name:"vacode/2.2-4331.md"},n=r('<h1 id="§-2-2-4331-contract-pricing-arrangements" tabindex="-1">§ 2.2-4331 Contract pricing arrangements. <a class="header-anchor" href="#§-2-2-4331-contract-pricing-arrangements" aria-label="Permalink to &quot;§ 2.2-4331 Contract pricing arrangements.&quot;">​</a></h1><p>A. Except as prohibited in this section, public contracts may be awarded on a fixed price or cost reimbursement basis, or on any other basis that is not prohibited.</p><p>B. Except in case of emergency affecting the public health, safety, or welfare, no public contract shall be awarded on the basis of cost plus a percentage of cost.</p><p>C. The following contract pricing arrangements shall not be prohibited by this section:</p><ol><li><p>A policy or contract of insurance or prepaid coverage having a premium computed on the basis of claims paid or incurred, plus the insurance carrier&#39;s administrative costs and retention stated in whole or part as a percentage of such claims; or</p></li><li><p>A cost plus a percentage of the private investment made by a private entity as a basis for the procurement of commercial or financial consulting services related to a qualifying transportation facility under the Public-Private Transportation Act of 1995 (§ <a href="http://law.lis.virginia.gov/vacode/33.2-1800/" target="_blank" rel="noreferrer">33.2-1800</a> et seq.) or a qualifying project under the Public-Private Education Facilities and Infrastructure Act of 2002 (§ <a href="http://law.lis.virginia.gov/vacode/56-575.1/" target="_blank" rel="noreferrer">56-575.1</a> et seq.) where the commercial or financial consulting services are sought to solicit or to solicit and evaluate proposals for the qualifying transportation facility or the qualifying project. As used in this section, &quot;private entity&quot; and &quot;qualifying transportation facility&quot; mean the same as those terms are defined in § <a href="http://law.lis.virginia.gov/vacode/33.2-1800/" target="_blank" rel="noreferrer">33.2-1800</a> and &quot;qualifying project&quot; means the same as that term is defined in § <a href="http://law.lis.virginia.gov/vacode/56-575.1/" target="_blank" rel="noreferrer">56-575.1</a>.</p></li></ol><p>1982, c. 647, § 11-43; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0496" target="_blank" rel="noreferrer">496</a>.</p>',6),o=[n];function c(s,l,p,h,f,d){return a(),t("div",null,o)}const m=e(i,[["render",c]]);export{u as __pageData,m as default};
