import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-4336 Bid bonds; construction contracts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4336.md","filePath":"vacode/2.2-4336.md"}'),a={name:"vacode/2.2-4336.md"},n=r('<h1 id="§-2-2-4336-bid-bonds-construction-contracts" tabindex="-1">§ 2.2-4336 Bid bonds; construction contracts. <a class="header-anchor" href="#§-2-2-4336-bid-bonds-construction-contracts" aria-label="Permalink to &quot;§ 2.2-4336 Bid bonds; construction contracts.&quot;">​</a></h1><p>A. Except in cases of emergency, all bids or proposals for nontransportation-related construction contracts in excess of $500,000 or transportation-related projects authorized under Article 2 (§ <a href="/vacode/33.2-208/">33.2-208</a> et seq.) of Chapter 2 of Title 33.2 that are in excess of $350,000 and partially or wholly funded by the Commonwealth shall be accompanied by a bid bond from a surety company selected by the bidder that is authorized to do business in Virginia, as a guarantee that if the contract is awarded to the bidder, he will enter into the contract for the work mentioned in the bid. The amount of the bid bond shall not exceed five percent of the amount bid.</p><p>B. No forfeiture under a bid bond shall exceed the lesser of (i) the difference between the bid for which the bond was written and the next low bid, or (ii) the face amount of the bid bond.</p><p>C. Nothing in this section shall preclude a public body from requiring bid bonds to accompany bids or proposals for construction contracts anticipated to be less than $500,000 for nontransportation-related projects or $350,000 for transportation-related projects authorized under Article 2 (§ <a href="/vacode/33.2-208/">33.2-208</a> et seq.) of Chapter 2 of Title 33.2 and partially or wholly funded by the Commonwealth.</p><p>1982, c. 647, § 11-57; 1984, c. 160; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0925" target="_blank" rel="noreferrer">925</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0789" target="_blank" rel="noreferrer">789</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0789" target="_blank" rel="noreferrer">789</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0413" target="_blank" rel="noreferrer">413</a>.</p>',5),i=[n];function c(s,d,l,h,f,p){return t(),o("div",null,i)}const _=e(a,[["render",c]]);export{u as __pageData,_ as default};
