import{_ as t,o as e,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-435.11 Special Assistant to the Governor for Coastal Adaptation and Protection; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-435.11.md","filePath":"vacode/2.2-435.11.md"}'),i={name:"vacode/2.2-435.11.md"},n=o('<h1 id="§-2-2-435-11-special-assistant-to-the-governor-for-coastal-adaptation-and-protection-duties" tabindex="-1">§ 2.2-435.11 Special Assistant to the Governor for Coastal Adaptation and Protection; duties. <a class="header-anchor" href="#§-2-2-435-11-special-assistant-to-the-governor-for-coastal-adaptation-and-protection-duties" aria-label="Permalink to &quot;§ 2.2-435.11 Special Assistant to the Governor for Coastal Adaptation and Protection; duties.&quot;">​</a></h1><p>A. The position of Special Assistant to the Governor for Coastal Adaptation and Protection (the Special Assistant) is created. The Special Assistant shall be the primary point of contact for the resources to address coastal adaptation and flooding mitigation. The Special Assistant shall be the lead in developing and in providing direction and ensuring accountability for a statewide coastal flooding adaptation strategy and shall initiate and assist with economic development opportunities associated with adaptation, development opportunities for the creation of business incubators, the advancement of the academic expertise at the Commonwealth Center for Recurrent Flooding Resiliency, coordination with the Virginia Growth and Opportunity Board, safeguarding strategic national assets threatened by coastal flooding, and pursuing federal, state, and local funding opportunities for adaptation initiatives.</p><p>B. In consultation with the Chief Resilience Officer designated pursuant to § <a href="http://law.lis.virginia.gov/vacode/title2.2/chapter2/section2.2-220.5/" target="_blank" rel="noreferrer">2.2-220.5</a>, the Special Assistant shall:</p><ol><li><p>Identify and monitor those areas of the Commonwealth that are at greatest risk from recurrent flooding and increased future flooding and recommend actions that both the private and public sectors should consider in order to increase the resilience of such areas;</p></li><li><p>Upon the request of any locality in the Commonwealth in which is located a substantial flood defense or catchment area, including a levee, reservoir, dam, catch basin, or wetland or lake improved or constructed for the purpose of flood control, review and comment on plans for the construction or substantial reinforcement of such flood defense or catchment area; and</p></li><li><p>Initiate and assist with the pursuit of funding opportunities for resilience initiatives at both the state and local levels and assist in overseeing and coordinating funding initiatives of all agencies of the Commonwealth.</p></li></ol><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0722" target="_blank" rel="noreferrer">722</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0723" target="_blank" rel="noreferrer">723</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0493" target="_blank" rel="noreferrer">493</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0786" target="_blank" rel="noreferrer">786</a>.</p>',5),r=[n];function s(l,c,d,p,h,f){return e(),a("div",null,r)}const v=t(i,[["render",s]]);export{g as __pageData,v as default};
