import{_ as o,o as a,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 2.2-4352 Prompt payment of bills by localities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4352.md","filePath":"vacode/2.2-4352.md"}'),i={name:"vacode/2.2-4352.md"},s=e("h1",{id:"§-2-2-4352-prompt-payment-of-bills-by-localities",tabindex:"-1"},[t("§ 2.2-4352 Prompt payment of bills by localities. "),e("a",{class:"header-anchor",href:"#§-2-2-4352-prompt-payment-of-bills-by-localities","aria-label":'Permalink to "§ 2.2-4352 Prompt payment of bills by localities."'},"​")],-1),n=e("p",null,"Every agency of local government that acquires goods or services, or conducts any other type of contractual business with a nongovernmental, privately owned enterprise, shall promptly pay for the completed delivered goods or services by the required payment date. The required payment date shall be either: (i) the date on which payment is due under the terms of the contract for the provision of the goods or services; or (ii) if a date is not established by contract, not more than forty-five days after goods or services are received or not more than forty-five days after the invoice is rendered, whichever is later.",-1),l=e("p",null,"Separate payment dates may be specified for contracts under which goods or services are provided in a series of partial executions or deliveries to the extent that the contract provides for separate payment for partial execution or delivery.",-1),c=e("p",null,"Within twenty days after the receipt of the invoice or goods or services, the agency shall notify the supplier of any defect or impropriety that would prevent payment by the payment date.",-1),p=e("p",null,"Unless otherwise provided under the terms of the contract for the provision of goods or services, every agency that fails to pay by the payment date shall pay any finance charges assessed by the supplier that shall not exceed one percent per month.",-1),d=e