import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-4362 Stay of award during protest.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4362.md","filePath":"vacode/2.2-4362.md"}'),o={name:"vacode/2.2-4362.md"},i=a('<h1 id="§-2-2-4362-stay-of-award-during-protest" tabindex="-1">§ 2.2-4362 Stay of award during protest. <a class="header-anchor" href="#§-2-2-4362-stay-of-award-during-protest" aria-label="Permalink to &quot;§ 2.2-4362 Stay of award during protest.&quot;">​</a></h1><p>An award need not be delayed for the period allowed a bidder or offeror to protest, but in the event of a timely protest as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-4360/" target="_blank" rel="noreferrer">2.2-4360</a>, or the filing of a timely legal action as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-4364/" target="_blank" rel="noreferrer">2.2-4364</a>, no further action to award the contract shall be taken unless there is a written determination that proceeding without delay is necessary to protect the public interest or unless the bid or offer would expire.</p><p>1982, c. 647, § 11-68; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0864" target="_blank" rel="noreferrer">864</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',3),n=[i];function d(s,l,c,p,f,_){return t(),r("div",null,n)}const u=e(o,[["render",d]]);export{g as __pageData,u as default};
