import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 2.2-4368 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4368.md","filePath":"vacode/2.2-4368.md"}'),i={name:"vacode/2.2-4368.md"},n=o('<h1 id="§-2-2-4368-definitions" tabindex="-1">§ 2.2-4368 Definitions. <a class="header-anchor" href="#§-2-2-4368-definitions" aria-label="Permalink to &quot;§ 2.2-4368 Definitions.&quot;">​</a></h1><p>As used in this article:</p><p>&quot;Immediate family&quot; means a spouse, children, parents, brothers and sisters, and any other person living in the same household as the employee.</p><p>&quot;Official responsibility&quot; means administrative or operating authority, whether intermediate or final, to initiate, approve, disapprove or otherwise affect a procurement transaction, or any claim resulting therefrom.</p><p>&quot;Pecuniary interest arising from the procurement&quot; means a personal interest in a contract as defined in the State and Local Government Conflict of Interests Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-3100/" target="_blank" rel="noreferrer">2.2-3100</a> et seq.).</p><p>&quot;Procurement transaction&quot; means all functions that pertain to the obtaining of any goods, services or construction, including description of requirements, selection and solicitation of sources, preparation and award of contract, and all phases of contract administration.</p><p>&quot;Public employee&quot; means any person employed by a public body, including elected officials or appointed members of governing bodies.</p><p>1982, c. 647, § 11-73; 1987, Sp. Sess., c. 1; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',8),r=[n];function s(c,p,l,d,f,m){return t(),a("div",null,r)}const _=e(i,[["render",s]]);export{h as __pageData,_ as default};
