import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 2.2-4369 Proscribed participation by public employees in procurement transactions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4369.md","filePath":"vacode/2.2-4369.md"}'),a={name:"vacode/2.2-4369.md"},o=t('<h1 id="§-2-2-4369-proscribed-participation-by-public-employees-in-procurement-transactions" tabindex="-1">§ 2.2-4369 Proscribed participation by public employees in procurement transactions. <a class="header-anchor" href="#§-2-2-4369-proscribed-participation-by-public-employees-in-procurement-transactions" aria-label="Permalink to &quot;§ 2.2-4369 Proscribed participation by public employees in procurement transactions.&quot;">​</a></h1><p>Except as may be specifically allowed by subdivisions B 1, 2, and 3 of § <a href="http://law.lis.virginia.gov/vacode/2.2-3112/" target="_blank" rel="noreferrer">2.2-3112</a>, no public employee having official responsibility for a procurement transaction shall participate in that transaction on behalf of the public body when the employee knows that:</p><ol><li><p>The employee is contemporaneously employed by a bidder, offeror or contractor involved in the procurement transaction;</p></li><li><p>The employee, the employee&#39;s partner, or any member of the employee&#39;s immediate family holds a position with a bidder, offeror or contractor such as an officer, director, trustee, partner or the like, or is employed in a capacity involving personal and substantial participation in the procurement transaction, or owns or controls an interest of more than five percent;</p></li><li><p>The employee, the employee&#39;s partner, or any member of the employee&#39;s immediate family has a pecuniary interest arising from the procurement transaction; or</p></li><li><p>The employee, the employee&#39;s partner, or any member of the employee&#39;s immediate family is negotiating, or has an arrangement concerning, prospective employment with a bidder, offeror or contractor.</p></li></ol><p>1982, c. 647, § 11-74; 1985, c. 565; 1987, Sp. Sess., c. 1; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0083" target="_blank" rel="noreferrer">83</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0694" target="_blank" rel="noreferrer">694</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0832" target="_blank" rel="noreferrer">832</a>.</p>',4),n=[o];function p(l,c,s,m,h,f){return r(),i("div",null,n)}const y=e(a,[["render",p]]);export{d as __pageData,y as default};
