import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 2.2-4402 Collateral for public deposits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4402.md","filePath":"vacode/2.2-4402.md"}'),i={name:"vacode/2.2-4402.md"},o=a('<h1 id="§-2-2-4402-collateral-for-public-deposits" tabindex="-1">§ 2.2-4402 Collateral for public deposits. <a class="header-anchor" href="#§-2-2-4402-collateral-for-public-deposits" aria-label="Permalink to &quot;§ 2.2-4402 Collateral for public deposits.&quot;">​</a></h1><p>Qualified public depositories shall elect to secure deposits by either the pooled method or the dedicated method. Every qualified public depository shall deposit with a qualified escrow agent eligible collateral equal to or in excess of the required collateral. Eligible collateral shall be valued as determined by the Treasury Board. Substitutions and withdrawals of eligible collateral may be made as determined by the Treasury Board.</p><p>Notwithstanding any other provisions of law, no qualified public depository shall be required to give bond or pledge securities or instruments in the manner herein provided for the purpose of securing deposits received or held in the trust department of the depository and that are secured as required by § <a href="http://law.lis.virginia.gov/vacode/6.2-1005/" target="_blank" rel="noreferrer">6.2-1005</a> of the Code of Virginia or that are secured pursuant to Title 12, § 92a of the United States Code by securities of the classes prescribed by § <a href="http://law.lis.virginia.gov/vacode/6.2-1005/" target="_blank" rel="noreferrer">6.2-1005</a> of the Code of Virginia.</p><p>No qualified public depository shall accept or retain any public deposit that is required to be secured unless it has deposited eligible collateral equal to its required collateral with a qualified escrow agent pursuant to this chapter.</p><p>1973, c. 172, § 2.1-362; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0640" target="_blank" rel="noreferrer">640</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>.</p>',5),l=[o];function s(d,n,c,p,h,u){return t(),r("div",null,l)}const g=e(i,[["render",s]]);export{b as __pageData,g as default};
