import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 2.2-4404 Procedure for payment of losses by dedicated method.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4404.md","filePath":"vacode/2.2-4404.md"}'),r={name:"vacode/2.2-4404.md"},i=a('<h1 id="§-2-2-4404-procedure-for-payment-of-losses-by-dedicated-method" tabindex="-1">§ 2.2-4404 Procedure for payment of losses by dedicated method. <a class="header-anchor" href="#§-2-2-4404-procedure-for-payment-of-losses-by-dedicated-method" aria-label="Permalink to &quot;§ 2.2-4404 Procedure for payment of losses by dedicated method.&quot;">​</a></h1><p>When the Treasury Board determines that a qualified public depository securing public deposits in accordance with this section is a defaulting depository, it shall as promptly as practicable take steps to reimburse public depositors of all uninsured public deposits using the following procedures:</p><ol><li><p>The Treasury Board shall ascertain the amount of uninsured public deposits held by the defaulting depository with the cooperation of the Commissioner of Financial Institutions, the receiver appointed for such depository or by any other means available.</p></li><li><p>The amount of such uninsured public deposits ascertained as provided in subdivision 1, plus any costs associated with liquidation of the eligible collateral of the defaulting depository, shall be assessed by the Treasury Board against the defaulting depository. The State Treasurer shall promptly take possession of the eligible collateral deposited by such depository with the depository&#39;s escrow agent, as is necessary to satisfy the assessment of the Treasury Board and shall liquidate the same and turn over the net proceeds to the Treasury Board.</p></li><li><p>Upon receipt from the State Treasurer of the eligible collateral liquidated, the Treasury Board shall reimburse the public depositors from the proceeds of the collateral up to the extent of the depository&#39;s deposit liability to them, net of any applicable deposit insurance.</p></li></ol><p>1984, c. 135, § 2.1-363.1; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0640" target="_blank" rel="noreferrer">640</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>.</p>',4),s=[i];function l(d,n,p,c,h,u){return t(),o("div",null,s)}const y=e(r,[["render",l]]);export{b as __pageData,y as default};
