import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-4405 Powers of Treasury Board relating to the administration of this chapter.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4405.md","filePath":"vacode/2.2-4405.md"}'),i={name:"vacode/2.2-4405.md"},o=a('<h1 id="§-2-2-4405-powers-of-treasury-board-relating-to-the-administration-of-this-chapter" tabindex="-1">§ 2.2-4405 Powers of Treasury Board relating to the administration of this chapter. <a class="header-anchor" href="#§-2-2-4405-powers-of-treasury-board-relating-to-the-administration-of-this-chapter" aria-label="Permalink to &quot;§ 2.2-4405 Powers of Treasury Board relating to the administration of this chapter.&quot;">​</a></h1><p>The Treasury Board shall have power to:</p><ol><li><p>Make and enforce regulations and guidelines necessary and proper to the full and complete performance of its functions under this chapter;</p></li><li><p>Prescribe and enforce regulations and guidelines fixing terms and conditions consistent with this chapter under which public deposits must be secured;</p></li><li><p>Require additional collateral, in excess of the required collateral of any or all qualified public depositories as it may determine prudent under the circumstances;</p></li><li><p>Determine what securities or instruments shall be acceptable as eligible collateral, and fix the percentage of face value or market value of such securities or instruments that can be used to secure public deposits;</p></li><li><p>Establish guidelines to permit banks to withdraw from the procedures for the payment of losses under § <a href="http://law.lis.virginia.gov/vacode/2.2-4403/" target="_blank" rel="noreferrer">2.2-4403</a> and instead be governed by the procedures for the payment of losses under § <a href="http://law.lis.virginia.gov/vacode/2.2-4404/" target="_blank" rel="noreferrer">2.2-4404</a>, consistent with the primary purpose of protecting public deposits;</p></li><li><p>Require any qualified public depository to provide information concerning its public deposits as requested by the Treasury Board; and</p></li><li><p>Determine when a default or insolvency has occurred and to take such action as it may deem advisable for the protection, collection, compromise or settlement of any claim arising in case of default or insolvency.</p></li></ol><p>1973, c. 172, § 2.1-364; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0640" target="_blank" rel="noreferrer">640</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>.</p>',4),n=[o];function s(l,c,d,p,h,f){return r(),t("div",null,n)}const m=e(i,[["render",s]]);export{g as __pageData,m as default};
