import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 2.2-5200 Intent and purpose; definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-5200.md","filePath":"vacode/2.2-5200.md"}'),a={name:"vacode/2.2-5200.md"},r=n('<h1 id="§-2-2-5200-intent-and-purpose-definitions" tabindex="-1">§ 2.2-5200 Intent and purpose; definitions. <a class="header-anchor" href="#§-2-2-5200-intent-and-purpose-definitions" aria-label="Permalink to &quot;§ 2.2-5200 Intent and purpose; definitions.&quot;">​</a></h1><p>A. It is the intention of this law to create a collaborative system of services and funding that is child-centered, family-focused and community-based when addressing the strengths and needs of troubled and at-risk youths and their families in the Commonwealth.</p><p>This law shall be interpreted and construed so as to effectuate the following purposes:</p><ol><li><p>Ensure that services and funding are consistent with the Commonwealth&#39;s policies of preserving families and providing appropriate services in the least restrictive environment, while protecting the welfare of children and maintaining the safety of the public;</p></li><li><p>Identify and intervene early with young children and their families who are at risk of developing emotional or behavioral problems, or both, due to environmental, physical or psychological stress;</p></li><li><p>Design and provide services that are responsive to the unique and diverse strengths and needs of troubled youths and families;</p></li><li><p>Increase interagency collaboration and family involvement in service delivery and management;</p></li><li><p>Encourage a public and private partnership in the delivery of services to troubled and at-risk youths and their families; and</p></li><li><p>Provide communities flexibility in the use of funds and to authorize communities to make decisions and be accountable for providing services in concert with these purposes.</p></li></ol><p>B. As used in this chapter, unless the context requires a different meaning:</p><p>&quot;CSA&quot; means the Children&#39;s Services Act.</p><p>&quot;Council&quot; means the State Executive Council for Children&#39;s Services created pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-2648/" target="_blank" rel="noreferrer">2.2-2648</a>.</p><p>1992, cc. 837, 880, § 2.1-745; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0366" target="_blank" rel="noreferrer">366</a>.</p>',8),o=[r];function s(l,d,c,p,h,f){return i(),t("div",null,o)}const m=e(a,[["render",s]]);export{v as __pageData,m as default};
