import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-523 Confidentiality of information; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-523.md","filePath":"vacode/2.2-523.md"}'),a={name:"vacode/2.2-523.md"},o=n('<h1 id="§-2-2-523-confidentiality-of-information-penalty" tabindex="-1">§ 2.2-523 Confidentiality of information; penalty. <a class="header-anchor" href="#§-2-2-523-confidentiality-of-information-penalty" aria-label="Permalink to &quot;§ 2.2-523 Confidentiality of information; penalty.&quot;">​</a></h1><p>A. The Office shall not make public, prior to a public hearing pursuant to § <a href="/vacode/2.2-520/">2.2-520</a>, investigative notes and other correspondence and information furnished to the Office in confidence with respect to an investigation or conciliation process involving an alleged unlawful discriminatory practice.</p><p>B. Nothing in this section, however, shall prohibit the distribution of information taken from inactive reports in a form that does not reveal the identity of the parties involved or other persons supplying information.</p><p>1987, c. 581, § 2.1-723; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>, § 2.2-2637; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0196" target="_blank" rel="noreferrer">196</a>.</p>',4),r=[o];function l(c,f,s,p,h,d){return i(),t("div",null,r)}const v=e(a,[["render",l]]);export{g as __pageData,v as default};
