import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 2.2-5303 Duties of participating agencies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-5303.md","filePath":"vacode/2.2-5303.md"}'),n={name:"vacode/2.2-5303.md"},s=a('<h1 id="§-2-2-5303-duties-of-participating-agencies" tabindex="-1">§ 2.2-5303 Duties of participating agencies. <a class="header-anchor" href="#§-2-2-5303-duties-of-participating-agencies" aria-label="Permalink to &quot;§ 2.2-5303 Duties of participating agencies.&quot;">​</a></h1><p>The duties of the participating agencies shall include:</p><ol><li><p>Establishing a statewide system of early intervention services in accordance with state and federal statutes and regulations;</p></li><li><p>Identifying and maximizing coordination of all available public and private resources for early intervention services;</p></li><li><p>Developing and implementing formal state interagency agreements that define the financial responsibility and service obligations of each participating agency for early intervention services, establish procedures for resolving disputes, and address any additional matters necessary to ensure collaboration;</p></li><li><p>Consulting with the lead agency in the promulgation of regulations to implement the early intervention services system, including developing definitions of eligibility and services;</p></li><li><p>Carrying out decisions resulting from the dispute resolution process;</p></li><li><p>Providing assistance to localities in the implementation of a comprehensive early intervention services system in accordance with state and federal statutes and regulations; and</p></li><li><p>Requesting and reviewing data and reports on the implementation of early intervention services from counterpart local agencies.</p></li></ol><p>1992, c. 771, § 2.1-763; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',4),r=[s];function o(l,c,p,d,g,f){return i(),t("div",null,r)}const m=e(n,[["render",o]]);export{h as __pageData,m as default};
