import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-5400 Short title; definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-5400.md","filePath":"vacode/2.2-5400.md"}'),i={name:"vacode/2.2-5400.md"},n=o('<h1 id="§-2-2-5400-short-title-definitions" tabindex="-1">§ 2.2-5400 Short title; definitions. <a class="header-anchor" href="#§-2-2-5400-short-title-definitions" aria-label="Permalink to &quot;§ 2.2-5400 Short title; definitions.&quot;">​</a></h1><p>A. This chapter shall be known as the Community Action Act.</p><p>B. As used in this chapter, unless the context requires a different meaning:</p><p>&quot;Community action agency&quot; means a local subdivision of the Commonwealth, a combination of political subdivisions, a separate public agency or a private nonprofit agency that has the authority under its applicable charter or laws to receive funds to support community action activities and other appropriate measures designed to identify and deal with the causes of poverty in the Commonwealth, and that is designated as a community action agency by federal law, federal regulations or the Governor.</p><p>&quot;Community action program budget&quot; means state funds, federal block grants and federal categorical grants that are received by the Commonwealth for community action activities.</p><p>&quot;Community action statewide organization&quot; means community action programs, organized on a statewide basis, to enhance the capability of community action agencies.</p><p>&quot;Designated agency&quot; means the agency designated by the Secretary of Health and Human Resources pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-5401/" target="_blank" rel="noreferrer">2.2-5401</a>.</p><p>&quot;Local share&quot; means cash or in-kind goods and services donated to community action agencies to carry out their responsibilities.</p><p>&quot;Low-income person&quot; means a person who is a member of a household with a gross annual income equal to or less than 125 percent of the poverty standard accepted by the federal agency designated to establish poverty guidelines.</p><p>&quot;Service area&quot; means the geographical area within the jurisdiction of a community action agency or a community action statewide organization.</p><p>1982, c. 667, §§ 2.1-587, 2.1-588; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',11),r=[n];function s(c,d,h,l,p,u){return t(),a("div",null,r)}const f=e(i,[["render",s]]);export{g as __pageData,f as default};
