import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 2.2-5402 Powers and duties of designated agency.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-5402.md","filePath":"vacode/2.2-5402.md"}'),i={name:"vacode/2.2-5402.md"},o=t('<h1 id="§-2-2-5402-powers-and-duties-of-designated-agency" tabindex="-1">§ 2.2-5402 Powers and duties of designated agency. <a class="header-anchor" href="#§-2-2-5402-powers-and-duties-of-designated-agency" aria-label="Permalink to &quot;§ 2.2-5402 Powers and duties of designated agency.&quot;">​</a></h1><p>The designated agency shall have the following powers and duties to:</p><ol><li><p>Coordinate state activities designed to reduce poverty.</p></li><li><p>Cooperate with agencies of the Commonwealth and the federal government in reducing poverty and implementing community, social and economic programs.</p></li><li><p>Receive and expend funds for any purpose authorized by this chapter.</p></li><li><p>Enter into contracts with and to award grants to public and private nonprofit agencies and organizations.</p></li><li><p>Develop a state plan based on needs identified by community action agencies and community action statewide organizations.</p></li><li><p>Fund community action agencies and community action statewide organizations and to adopt regulations.</p></li><li><p>Provide assistance to local governments or private organizations for the purpose of establishing and operating a community action agency.</p></li><li><p>Provide technical assistance to community action agencies to improve program planning, program development, administration and the mobilization of public and private resources.</p></li><li><p>Require community action agencies and community action statewide organizations to generate local contributions of cash or in-kind services as the agency may establish by regulation.</p></li><li><p>Convene public meetings that provide citizens the opportunity to comment on public policies and programs to reduce poverty.</p></li><li><p>Advise the Governor and the General Assembly of the nature and extent of poverty in the Commonwealth and to make recommendations concerning changes in state and federal policies and programs.</p></li></ol><p>1982, c. 667, § 2.1-590; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',4),r=[o];function s(d,c,p,l,m,g){return a(),n("div",null,r)}const f=e(i,[["render",s]]);export{h as __pageData,f as default};
