import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-5507 Enforcement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-5507.md","filePath":"vacode/2.2-5507.md"}'),n={name:"vacode/2.2-5507.md"},o=a('<h1 id="§-2-2-5507-enforcement" tabindex="-1">§ 2.2-5507 Enforcement. <a class="header-anchor" href="#§-2-2-5507-enforcement" aria-label="Permalink to &quot;§ 2.2-5507 Enforcement.&quot;">​</a></h1><p>The Department shall enforce the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/2.2-5503/" target="_blank" rel="noreferrer">2.2-5503</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-5506/" target="_blank" rel="noreferrer">2.2-5506</a>. Actions to enforce this chapter by injunctive and any other relief appropriate for enforcement may be filed in the Circuit Court of the City of Richmond or in any county or municipality where a violation occurred in whole or in part. In an enforcement action under this chapter, if it is determined that a person commenced a regulated introduction and did not comply with § <a href="http://law.lis.virginia.gov/vacode/2.2-5503/" target="_blank" rel="noreferrer">2.2-5503</a>, the court may enter an injunction directing the person to cease the regulated introduction and may order any additional action necessary to protect human health and the environment.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0472" target="_blank" rel="noreferrer">472</a>, § 2.1-776; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',3),i=[o];function c(l,d,h,s,f,p){return t(),r("div",null,i)}const g=e(n,[["render",c]]);export{m as __pageData,g as default};
