import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 2.2-5513 Responsibilities of Governor to ensure efficiency in government.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-5513.md","filePath":"vacode/2.2-5513.md"}'),a={name:"vacode/2.2-5513.md"},o=r('<h1 id="§-2-2-5513-responsibilities-of-governor-to-ensure-efficiency-in-government" tabindex="-1">§ 2.2-5513 Responsibilities of Governor to ensure efficiency in government. <a class="header-anchor" href="#§-2-2-5513-responsibilities-of-governor-to-ensure-efficiency-in-government" aria-label="Permalink to &quot;§ 2.2-5513 Responsibilities of Governor to ensure efficiency in government.&quot;">​</a></h1><p>A. The Governor shall cause to be conducted an examination of the commercial activities that are being performed by state employees at state agencies and institutions to ensure such activities are being accomplished in the most cost-efficient and effective manner.</p><p>B. The examination required by subsection A shall be completed at least once in every two-year period and may be conducted entirely by a commercial source through a solicitation process as provided in the Virginia Public Procurement Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4300/" target="_blank" rel="noreferrer">2.2-4300</a> et seq.) or the Public-Private Education Facilities and Infrastructure Act of 2002 (§ <a href="http://law.lis.virginia.gov/vacode/56-575.1/" target="_blank" rel="noreferrer">56-575.1</a> et seq.).</p><p>C. The examination required by subsection A shall consider at least three commercial activities as the Governor or the commercial source may identify.</p><p>D. Upon determination that outsourcing a commercial activity may result in reduced costs or otherwise provide a measurable benefit to the Commonwealth and to assure such activities are being accomplished in the most cost efficient and effective manner, the Governor shall cause that commercial activity to be competed in accordance with the Virginia Public Procurement Act or by using the processes described in the Public-Private Education Facilities and Infrastructure Act of 2002 (§ <a href="http://law.lis.virginia.gov/vacode/56-575.1/" target="_blank" rel="noreferrer">56-575.1</a> et seq.).</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0994" target="_blank" rel="noreferrer">994</a>.</p>',6),n=[o];function c(s,l,d,h,f,m){return t(),i("div",null,n)}const v=e(a,[["render",c]]);export{p as __pageData,v as default};
