import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 20-146.13 Exclusive, continuing jurisdiction.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-146.13.md","filePath":"vacode/20-146.13.md"}'),n={name:"vacode/20-146.13.md"},r=a('<h1 id="§-20-146-13-exclusive-continuing-jurisdiction" tabindex="-1">§ 20-146.13 Exclusive, continuing jurisdiction. <a class="header-anchor" href="#§-20-146-13-exclusive-continuing-jurisdiction" aria-label="Permalink to &quot;§ 20-146.13 Exclusive, continuing jurisdiction.&quot;">​</a></h1><p>A. Except as otherwise provided in § <a href="http://law.lis.virginia.gov/vacode/20-146.15/" target="_blank" rel="noreferrer">20-146.15</a>, a court of the Commonwealth that has made a child custody determination consistent with § <a href="http://law.lis.virginia.gov/vacode/20-146.12/" target="_blank" rel="noreferrer">20-146.12</a> or <a href="http://law.lis.virginia.gov/vacode/20-146.14/" target="_blank" rel="noreferrer">20-146.14</a> has exclusive, continuing jurisdiction as long as the child, a parent of the child, or any person acting as a parent of the child continues to live in the Commonwealth.</p><p>B. A court of the Commonwealth that has made a child custody determination and does not have exclusive, continuing jurisdiction under this section may modify that determination only if it has jurisdiction to make an initial determination under § <a href="http://law.lis.virginia.gov/vacode/20-146.12/" target="_blank" rel="noreferrer">20-146.12</a>.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0179" target="_blank" rel="noreferrer">179</a>.</p>',4),o=[r];function c(s,l,d,h,u,_){return i(),t("div",null,o)}const p=e(n,[["render",c]]);export{v as __pageData,p as default};
