import{_ as t,o as e,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 20-146.4 International application.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-146.4.md","filePath":"vacode/20-146.4.md"}'),n={name:"vacode/20-146.4.md"},o=i('<h1 id="§-20-146-4-international-application" tabindex="-1">§ 20-146.4 International application. <a class="header-anchor" href="#§-20-146-4-international-application" aria-label="Permalink to &quot;§ 20-146.4 International application.&quot;">​</a></h1><p>A. A court of this Commonwealth shall treat a foreign country as if it were a state of the United States for purposes of applying this article and Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/20-146.12/" target="_blank" rel="noreferrer">20-146.12</a> et seq.) of this chapter.</p><p>B. Except as otherwise provided in subsection C, a child custody determination made in a foreign country under factual circumstances in substantial conformity with the jurisdictional standards of this act must be recognized and enforced under Article 3 (§ <a href="http://law.lis.virginia.gov/vacode/20-146.22/" target="_blank" rel="noreferrer">20-146.22</a> et seq.) of this chapter.</p><p>C. A court of this Commonwealth need not apply this act if the child custody law of a foreign country violates fundamental principles of human rights.</p><p>1979, c. 229, § 20-146; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>.</p>',5),r=[o];function c(s,l,p,d,h,f){return e(),a("div",null,r)}const m=t(n,[["render",c]]);export{u as __pageData,m as default};
