import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 20-146.7 Notice to persons outside state.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-146.7.md","filePath":"vacode/20-146.7.md"}'),r={name:"vacode/20-146.7.md"},i=a('<h1 id="§-20-146-7-notice-to-persons-outside-state" tabindex="-1">§ 20-146.7 Notice to persons outside state. <a class="header-anchor" href="#§-20-146-7-notice-to-persons-outside-state" aria-label="Permalink to &quot;§ 20-146.7 Notice to persons outside state.&quot;">​</a></h1><p>A. Notice required for the exercise of jurisdiction when a person is outside this Commonwealth may be given in a manner prescribed by the law of this Commonwealth for service of process or by the law of the state in which the service is attempted or made. Notice may also be by certified or registered mail, return receipt requested, addressed to the last known address of the person to be served. Notice must be given in a manner reasonably calculated to give actual notice and an opportunity to be heard but may be by publication pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/8.01-316/" target="_blank" rel="noreferrer">8.01-316</a> and <a href="http://law.lis.virginia.gov/vacode/8.01-317/" target="_blank" rel="noreferrer">8.01-317</a> if other means are not effective.</p><p>B. Proof of service may be made in the manner prescribed by the law of this Commonwealth or by the law of the state in which the service is made.</p><p>C. Notice is not required for the exercise of jurisdiction with respect to a person who submits to the jurisdiction of the court.</p><p>1979, c. 229; 1982, c. 483, § 20-128; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>.</p>',5),s=[i];function n(c,d,h,l,p,f){return t(),o("div",null,s)}const u=e(r,[["render",n]]);export{m as __pageData,u as default};
