import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 20-45.1 Void and voidable marriages.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-45.1.md","filePath":"vacode/20-45.1.md"}'),i={name:"vacode/20-45.1.md"},o=t('<h1 id="§-20-45-1-void-and-voidable-marriages" tabindex="-1">§ 20-45.1 Void and voidable marriages. <a class="header-anchor" href="#§-20-45-1-void-and-voidable-marriages" aria-label="Permalink to &quot;§ 20-45.1 Void and voidable marriages.&quot;">​</a></h1><p>A. All marriages that are prohibited by § <a href="http://law.lis.virginia.gov/vacode/20-38.1/" target="_blank" rel="noreferrer">20-38.1</a> are void.</p><p>B. All marriages solemnized when either of the parties lacked capacity to consent to the marriage at the time the marriage was solemnized, because of mental incapacity or infirmity, shall be void from the time they shall be so declared by a decree of divorce or nullity.</p><p>C. All marriages solemnized on or after July 1, 2016, when either or both of the parties were, at the time of the solemnization, under the age of 18 and have not been emancipated as required by § <a href="http://law.lis.virginia.gov/vacode/20-48/" target="_blank" rel="noreferrer">20-48</a> shall be void from the time they shall be so declared by a decree of divorce or nullity. Notwithstanding the foregoing, this section shall not apply to a lawful marriage entered in another state or country prior to the parties being domiciled in the Commonwealth.</p><p>1975, c. 644; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0457" target="_blank" rel="noreferrer">457</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0543" target="_blank" rel="noreferrer">543</a>.</p>',5),n=[o];function l(d,s,h,c,m,p){return a(),r("div",null,n)}const f=e(i,[["render",l]]);export{_ as __pageData,f as default};
