import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 20-88.35 Bases for jurisdiction over nonresident.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-88.35.md","filePath":"vacode/20-88.35.md"}'),o={name:"vacode/20-88.35.md"},a=t('<h1 id="§-20-88-35-bases-for-jurisdiction-over-nonresident" tabindex="-1">§ 20-88.35 Bases for jurisdiction over nonresident. <a class="header-anchor" href="#§-20-88-35-bases-for-jurisdiction-over-nonresident" aria-label="Permalink to &quot;§ 20-88.35 Bases for jurisdiction over nonresident.&quot;">​</a></h1><p>In a proceeding to establish or enforce a support order or to determine parentage of a child, a tribunal of the Commonwealth may exercise personal jurisdiction over a nonresident individual or the individual&#39;s guardian or conservator if:</p><ol><li><p>The individual is personally served with process within the Commonwealth;</p></li><li><p>The individual submits to the jurisdiction of the Commonwealth by consent, by entering a general appearance, or by filing a responsive document having the effect of waiving any contest to personal jurisdiction;</p></li><li><p>The individual resided with the child in the Commonwealth;</p></li><li><p>The individual resided in the Commonwealth and paid prenatal expenses or provided support for the child;</p></li><li><p>The child resides in the Commonwealth as a result of the acts or directives of the individual;</p></li><li><p>The individual engaged in sexual intercourse in the Commonwealth and the child may have been conceived by the act of intercourse;</p></li><li><p>The individual asserted parentage of a child in the Virginia Birth Father Registry maintained in the Commonwealth by the Department of Social Services;</p></li><li><p>The exercise of personal jurisdiction is authorized under subdivision A 8 of § <a href="http://law.lis.virginia.gov/vacode/8.01-328.1/" target="_blank" rel="noreferrer">8.01-328.1</a>; or</p></li><li><p>There is any other basis consistent with the constitutions of the Commonwealth and the United States for the exercise of personal jurisdiction.</p></li></ol><p>The bases of personal jurisdiction set forth in this section or any other law of the Commonwealth may not be used to acquire personal jurisdiction for a tribunal of the Commonwealth to modify a child support order issued by a tribunal of another state unless the requirements of § <a href="http://law.lis.virginia.gov/vacode/20-88.76/" target="_blank" rel="noreferrer">20-88.76</a> or <a href="http://law.lis.virginia.gov/vacode/20-88.77:3/" target="_blank" rel="noreferrer">20-88.77:3</a> are met.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0673" target="_blank" rel="noreferrer">673</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0727" target="_blank" rel="noreferrer">727</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0200" target="_blank" rel="noreferrer">200</a>.</p>',5),n=[a];function s(l,d,h,c,p,f){return i(),r("div",null,n)}const g=e(o,[["render",s]]);export{v as __pageData,g as default};
