import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 20-88.40 Continuing jurisdiction to enforce child support order.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-88.40.md","filePath":"vacode/20-88.40.md"}'),i={name:"vacode/20-88.40.md"},n=o('<h1 id="§-20-88-40-continuing-jurisdiction-to-enforce-child-support-order" tabindex="-1">§ 20-88.40 Continuing jurisdiction to enforce child support order. <a class="header-anchor" href="#§-20-88-40-continuing-jurisdiction-to-enforce-child-support-order" aria-label="Permalink to &quot;§ 20-88.40 Continuing jurisdiction to enforce child support order.&quot;">​</a></h1><p>A. A tribunal of the Commonwealth that has issued a child support order consistent with the law of the Commonwealth may serve as an initiating tribunal to request a tribunal of another state to enforce:</p><ol><li><p>The order if the order is the controlling order and has not been modified by a tribunal of another state that assumed jurisdiction pursuant to this chapter; or</p></li><li><p>A money judgment for arrears of support and interest on the order accrued before a determination that an order of a tribunal of another state is the controlling order.</p></li></ol><p>B. A tribunal of the Commonwealth having continuing jurisdiction over a support order may act as a responding tribunal to enforce the order.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0673" target="_blank" rel="noreferrer">673</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0727" target="_blank" rel="noreferrer">727</a>.</p>',5),a=[n];function s(c,d,l,h,p,u){return t(),r("div",null,a)}const g=e(i,[["render",s]]);export{_ as __pageData,g as default};
