import{_ as o,o as t,c as e,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 20-88.43:2 Continuing, exclusive jurisdiction to modify spousal support order.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-88.43:2.md","filePath":"vacode/20-88.43:2.md"}'),r={name:"vacode/20-88.43:2.md"},s=i('<h1 id="§-20-88-43-2-continuing-exclusive-jurisdiction-to-modify-spousal-support-order" tabindex="-1">§ 20-88.43:2 Continuing, exclusive jurisdiction to modify spousal support order. <a class="header-anchor" href="#§-20-88-43-2-continuing-exclusive-jurisdiction-to-modify-spousal-support-order" aria-label="Permalink to &quot;§ 20-88.43:2 Continuing, exclusive jurisdiction to modify spousal support order.&quot;">​</a></h1><p>A. A court of the Commonwealth issuing a spousal support order consistent with the law of the Commonwealth has continuing, exclusive jurisdiction to modify the spousal support order throughout the existence of the support obligation.</p><p>B. A court of the Commonwealth may not modify a spousal support order issued by a tribunal of another state or a foreign country having continuing, exclusive jurisdiction over that order under the law of that state or foreign country.</p><p>C. A court of the Commonwealth that has continuing, exclusive jurisdiction over a spousal support order may serve as:</p><ol><li><p>An initiating court to request a tribunal of another state to enforce the spousal support order issued in the Commonwealth; or</p></li><li><p>A responding court to enforce or modify its own spousal support order.</p></li></ol><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0727" target="_blank" rel="noreferrer">727</a>.</p>',6),n=[s];function a(u,p,c,l,d,h){return t(),e("div",null,n)}const m=o(r,[["render",a]]);export{_ as __pageData,m as default};
