import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 20-88.53 Duties of state information agency.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-88.53.md","filePath":"vacode/20-88.53.md"}'),i={name:"vacode/20-88.53.md"},n=o('<h1 id="§-20-88-53-duties-of-state-information-agency" tabindex="-1">§ 20-88.53 Duties of state information agency. <a class="header-anchor" href="#§-20-88-53-duties-of-state-information-agency" aria-label="Permalink to &quot;§ 20-88.53 Duties of state information agency.&quot;">​</a></h1><p>A. The Department of Social Services is the state information agency under this chapter.</p><p>B. The state information agency shall:</p><ol><li><p>Compile and maintain a current list, including addresses, of the tribunals in the Commonwealth that have jurisdiction under this chapter and any support enforcement agencies in the Commonwealth and transmit a copy to the state information agency of every other state;</p></li><li><p>Maintain a register of names and addresses of tribunals and support enforcement agencies received from other states;</p></li><li><p>Forward to the appropriate tribunal in the county or city in the Commonwealth in which the obligee who is an individual or the obligor resides, or in which the obligor&#39;s property is believed to be located, all documents concerning a proceeding under this chapter received from another state or a foreign country; and</p></li><li><p>Obtain information concerning the location of the obligor and the obligor&#39;s property within the Commonwealth not exempt from execution, by such means as postal verification and federal or state locator services, examination of telephone directories, requests for the obligor&#39;s address from employers, and examination of governmental records, including, to the extent not prohibited by other law, those relating to real property, vital statistics, law enforcement, taxation, motor vehicles, driver&#39;s licenses, and social security.</p></li></ol><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0673" target="_blank" rel="noreferrer">673</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0727" target="_blank" rel="noreferrer">727</a>.</p>',5),r=[n];function s(c,l,h,d,p,f){return t(),a("div",null,r)}const u=e(i,[["render",s]]);export{g as __pageData,u as default};
