import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 20-88.71 Procedure to contest validity or enforcement of registered support order.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-88.71.md","filePath":"vacode/20-88.71.md"}'),a={name:"vacode/20-88.71.md"},i=o('<h1 id="§-20-88-71-procedure-to-contest-validity-or-enforcement-of-registered-support-order" tabindex="-1">§ 20-88.71 Procedure to contest validity or enforcement of registered support order. <a class="header-anchor" href="#§-20-88-71-procedure-to-contest-validity-or-enforcement-of-registered-support-order" aria-label="Permalink to &quot;§ 20-88.71 Procedure to contest validity or enforcement of registered support order.&quot;">​</a></h1><p>A. A nonregistering party seeking to contest the validity or enforcement of a registered support order in the Commonwealth shall request a hearing within the time required by § <a href="http://law.lis.virginia.gov/vacode/20-88.70/" target="_blank" rel="noreferrer">20-88.70</a>. The nonregistering party may seek to vacate the registration, to assert any defense to an allegation of noncompliance with the registered order, or to contest the remedies being sought or the amount of any alleged arrearages pursuant to § <a href="http://law.lis.virginia.gov/vacode/20-88.72/" target="_blank" rel="noreferrer">20-88.72</a>.</p><p>B. If the nonregistering party fails to contest the validity or enforcement of the registered support order in a timely manner, the order is confirmed by operation of law.</p><p>C. If a nonregistering party requests a hearing to contest the validity or enforcement of the registered support order, the registering tribunal shall schedule the matter for hearing and give notice to the parties of the date, time, and place of the hearing.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0673" target="_blank" rel="noreferrer">673</a>; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0797" target="_blank" rel="noreferrer">797</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0897" target="_blank" rel="noreferrer">897</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0727" target="_blank" rel="noreferrer">727</a>.</p>',5),n=[i];function s(l,c,d,g,p,h){return r(),t("div",null,n)}const u=e(a,[["render",s]]);export{_ as __pageData,u as default};
