import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 20-88.74 Procedure to register child support order of another state for modification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-88.74.md","filePath":"vacode/20-88.74.md"}'),a={name:"vacode/20-88.74.md"},i=o('<h1 id="§-20-88-74-procedure-to-register-child-support-order-of-another-state-for-modification" tabindex="-1">§ 20-88.74 Procedure to register child support order of another state for modification. <a class="header-anchor" href="#§-20-88-74-procedure-to-register-child-support-order-of-another-state-for-modification" aria-label="Permalink to &quot;§ 20-88.74 Procedure to register child support order of another state for modification.&quot;">​</a></h1><p>A party or support enforcement agency seeking to modify, or to modify and enforce, a child support order issued in another state shall register that order in this Commonwealth in the same manner as provided in Article 8 (§ <a href="http://law.lis.virginia.gov/vacode/20-88.66/" target="_blank" rel="noreferrer">20-88.66</a> et seq.) if the order has not been registered. A petition for modification may be filed at the same time as a request for registration, or later. The pleading must specify the grounds for modification.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0673" target="_blank" rel="noreferrer">673</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0069" target="_blank" rel="noreferrer">69</a>.</p>',3),n=[i];function s(d,c,f,p,l,h){return r(),t("div",null,n)}const g=e(a,[["render",s]]);export{m as __pageData,g as default};
