import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 20-88.89 Contest of registered convention support order.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-88.89.md","filePath":"vacode/20-88.89.md"}'),n={name:"vacode/20-88.89.md"},a=r('<h1 id="§-20-88-89-contest-of-registered-convention-support-order" tabindex="-1">§ 20-88.89 Contest of registered convention support order. <a class="header-anchor" href="#§-20-88-89-contest-of-registered-convention-support-order" aria-label="Permalink to &quot;§ 20-88.89 Contest of registered convention support order.&quot;">​</a></h1><p>A. Except as otherwise provided in this chapter, §§ <a href="http://law.lis.virginia.gov/vacode/20-88.71/" target="_blank" rel="noreferrer">20-88.71</a>, <a href="http://law.lis.virginia.gov/vacode/20-88.72/" target="_blank" rel="noreferrer">20-88.72</a>, and <a href="http://law.lis.virginia.gov/vacode/20-88.73/" target="_blank" rel="noreferrer">20-88.73</a> apply to a contest of a registered Convention support order.</p><p>B. A party contesting a registered Convention support order shall file a contest not later than 30 days after notice of the registration, but if the contesting party does not reside in the United States, the contest must be filed not later than 60 days after notice of the registration.</p><p>C. If the non-registering party fails to contest the registered Convention support order within the time period specified in subsection B, the order is enforceable.</p><p>D. A contest of a registered Convention support order may be based only on grounds set forth in § <a href="http://law.lis.virginia.gov/vacode/20-88.90/" target="_blank" rel="noreferrer">20-88.90</a>. The contesting party bears the burden of proof.</p><p>E. In a contest of a registered Convention support order, a tribunal of the Commonwealth:</p><ol><li><p>Is bound by the findings of fact on which the foreign tribunal based its jurisdiction; and</p></li><li><p>May not review the merits of the order.</p></li></ol><p>F. A tribunal of the Commonwealth deciding a contest of a registered Convention support order shall promptly notify the parties of its decision.</p><p>G. A challenge or appeal, if any, does not stay the enforcement of a Convention support order unless there are exceptional circumstances.</p><p>2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0727" target="_blank" rel="noreferrer">727</a>.</p>',10),i=[a];function s(p,d,c,l,f,h){return t(),o("div",null,i)}const v=e(n,[["render",s]]);export{_ as __pageData,v as default};
