import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 20-90 Suit to affirm marriage.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/20-90.md","filePath":"vacode/20-90.md"}'),o={name:"vacode/20-90.md"},i=r('<h1 id="§-20-90-suit-to-affirm-marriage" tabindex="-1">§ 20-90 Suit to affirm marriage. <a class="header-anchor" href="#§-20-90-suit-to-affirm-marriage" aria-label="Permalink to &quot;§ 20-90 Suit to affirm marriage.&quot;">​</a></h1><p>A. When the validity of any marriage shall be denied or doubted by either of the parties, the other party may institute a suit for affirmance of the marriage, and upon due proof of the validity thereof, it shall be decreed to be valid, and such decree shall be conclusive upon all persons concerned.</p><p>B. Notwithstanding § <a href="http://law.lis.virginia.gov/vacode/20-13/" target="_blank" rel="noreferrer">20-13</a>, a marriage of a couple where one of the parties was under the age of 18 at the time of solemnization may be decreed valid upon petition by the party who was under the age of 18 at the time of the solemnization that would otherwise be deemed voidable under subsection C of § <a href="http://law.lis.virginia.gov/vacode/20-45.1/" target="_blank" rel="noreferrer">20-45.1</a> solely because of age, once such party has attained the age of 18. If both parties were under the age of 18 at the time of solemnization, such petition shall not be granted unless both parties have reached the age of 18 and join in the petition together.</p><p>Code 1919, § 5102; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0457" target="_blank" rel="noreferrer">457</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0543" target="_blank" rel="noreferrer">543</a>.</p>',4),n=[i];function s(h,l,d,f,c,p){return a(),t("div",null,n)}const m=e(o,[["render",s]]);export{u as __pageData,m as default};
