import{_ as i,o as n,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 21-117.1 Abolishing sanitary districts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/21-117.1.md","filePath":"vacode/21-117.1.md"}'),a={name:"vacode/21-117.1.md"},s=e("h1",{id:"§-21-117-1-abolishing-sanitary-districts",tabindex:"-1"},[t("§ 21-117.1 Abolishing sanitary districts. "),e("a",{class:"header-anchor",href:"#§-21-117-1-abolishing-sanitary-districts","aria-label":'Permalink to "§ 21-117.1 Abolishing sanitary districts."'},"​")],-1),r=e("p",null,"Any sanitary district heretofore or hereafter created in any county under the provisions of the preceding sections of this article may be abolished by ordinance adopted by the governing body of such county, upon the petition of no less than 50 qualified voters residing within the boundaries of the district desired to be abolished or, if the district contains less than 100 qualified voters, upon petition of 50 percent of the qualified voters residing within the boundaries of such district.",-1),h=e("p",null,"Upon filing of the petition, the governing body of the county shall fix a day for a hearing on the question of abolishing the sanitary district, which hearing shall embrace a consideration of whether the property in the sanitary district will or will not be benefited by the abolition thereof, and the governing body of the county shall be fully informed as to the obligations and functions of the sanitary district. Notice of such hearing shall be given by publication once a week for three consecutive weeks in some newspaper of general circulation within the county to be designated by the governing body of the county. At least 10 days shall intervene between the completion of the publication and the date set for hearing, and such publication shall be considered complete on the twenty-first day after the first publication, and no such district shall be abolished until the notice has been given and the hearing had.",-1),d=e("p",null,"Any interested parties may appear and be heard on any matters pertaining to the subject of the hearing.",-1),c=e("p",null,"Upon the hearing, such ordinance shall be adopted as to the governing body of the county may seem equitable and proper, concerning the abolition of the district and as to the funds on hand to the credit of the district, provided, however, that no such ordinance shall be adopted abolishing the sanitary district unless any bonds of the sanitary district that have theretofore been issued have been redeemed and the purposes for which the sanitary district was created have been completed, or unless all obligations and functions of the sanitary district have been taken over by the county as a whole, or unless the purposes for which the sanitary district was created are impractical or impossible of accomplishment and no obligations have been incurred by said sanitary district.",-1),l=e("p",null,[t("1954, c. 135; 2017, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0014",target:"_blank",rel:"noreferrer"},"14"),t(".")],-1),b=[s,r,h,d,c,l];function f(p,u,y,g,v,_){return n(),o("div",null,b)}const k=i(a,[["render",f]]);export{w as __pageData,k as default};
