import{_ as t,o,c as a,z as e,a as s}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 21-237 Creation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/21-237.md","filePath":"vacode/21-237.md"}'),i={name:"vacode/21-237.md"},r=e("h1",{id:"§-21-237-creation",tabindex:"-1"},[s("§ 21-237 Creation. "),e("a",{class:"header-anchor",href:"#§-21-237-creation","aria-label":'Permalink to "§ 21-237 Creation."'},"​")],-1),n=e("p",null,'In and for each district heretofore or hereafter created pursuant to this chapter or pursuant to a special act of the General Assembly, a commission is hereby created as a body corporate, invested with the rights, powers and authority and charged with the duties set forth in this chapter. The commission shall be known by the name of the district, except that the word "commission" shall be substituted for the word "district." Except as a special act creating the district shall otherwise provide, the commission shall consist of seven members, residents of the district, appointed by the State Health Commissioner, in the manner hereinafter provided, who shall notify each governing body immediately after such appointments. Representation on the commission shall be apportioned among the political segments of the district on a population basis so far as it may be practical to do so; but each political segment, however, shall be entitled to at least one representative on the commission; and all appointments thereto shall be made by the State Health Commissioner from a list of not less than one nor more than five names furnished to him by the respective governing bodies of each political segment of the district. Except as a special act creating the district shall otherwise provide, in making the original appointments, one of the members shall be appointed for a term of one year, two for a term of two years, two for a term of three years, and two for terms of four years each; subsequent appointments shall be made for a term of four years, except appointments to fill vacancies which shall be for the unexpired terms. Members of the commission shall hold office until their successors have been duly appointed and qualified.',-1),h=e("p",null,"1946, p. 350; Michie Suppl. 1946, § 1560iii4.",-1),c=[r,n,h];function l(d,m,p,f,u,b){return o(),a("div",null,c)}const y=t(i,[["render",l]]);export{v as __pageData,y as default};
