import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 21-307 Publication against unknown owners.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/21-307.md","filePath":"vacode/21-307.md"}'),r={name:"vacode/21-307.md"},i=e("h1",{id:"§-21-307-publication-against-unknown-owners",tabindex:"-1"},[a("§ 21-307 Publication against unknown owners. "),e("a",{class:"header-anchor",href:"#§-21-307-publication-against-unknown-owners","aria-label":'Permalink to "§ 21-307 Publication against unknown owners."'},"​")],-1),s=e("p",null,"If, at the time of the filing of the petition, or at any time subsequent thereto, it shall be made to appear to the court, or the judge thereof in vacation, by affidavit or otherwise, that the owner or owners of the whole or any share of any tract or tracts of land affected or within the bounds of the proposed drainage district, whose names are unknown, and cannot, after due diligence, be ascertained by the petitioners, the court or the judge thereof, shall order a notice in the nature of a summons to be given to all such persons by publication of the petition, or of the substance thereof, and describing generally the tract or tracts of land as to which the owner or owners are unknown, with the order of the court or the judge thereof thereon, in some newspaper published in the county wherein the land is located or in any city within the geographical limits of the county. If no newspaper shall be published in the county or city, then in some newspaper designated in the order of the court or the judge thereof, and a copy of such publication shall also be posted in at least three conspicuous places within the boundaries of the proposed district, and at the courthouse door of the county. Such publication in a newspaper shall be made once a week for four successive weeks.",-1),h=e("p",null,"Code 1919, § 1740; 1926, p. 608.",-1),c=[i,s,h];function d(l,u,p,f,w,b){return o(),n("div",null,c)}