import{_ as t,o as n,c as i,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 21-310 Guardian ad litem for infants and incapacitated adults.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/21-310.md","filePath":"vacode/21-310.md"}'),o={name:"vacode/21-310.md"},r=e("h1",{id:"§-21-310-guardian-ad-litem-for-infants-and-incapacitated-adults",tabindex:"-1"},[a("§ 21-310 Guardian ad litem for infants and incapacitated adults. "),e("a",{class:"header-anchor",href:"#§-21-310-guardian-ad-litem-for-infants-and-incapacitated-adults","aria-label":'Permalink to "§ 21-310 Guardian ad litem for infants and incapacitated adults."'},"​")],-1),s=e("p",null,"These proceedings shall not be stayed because of infancy or incapacity; but the court in which the petition was filed shall appoint some discreet and competent attorney at law as guardian ad litem to any infant or incapacitated person who may own or be interested in any of the land affected by these proceedings, whether such persons have been served with process or not. If no such attorney is found willing to act, the court shall appoint some other discreet and proper person as guardian ad litem, but the person so appointed shall not be liable to costs. Every guardian ad litem shall faithfully represent the interest or estate of the infant or incapacitated person for whom he is appointed, and it shall be the duty of the court to see that the estate of such defendant is so represented and protected. Wherever the court is of opinion that the interests of any infant or incapacitated person require, it shall remove any guardian ad litem and appoint another in his stead.",-1),d=e("p",null,[a("Code 1919, § 1741; 1997, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0801",target:"_blank",rel:"noreferrer"},"801"),a(".")],-1),c=[r,s,d];function l(h,p,f,u,m,_){return n(),i("div",null,c)}const y=t(o,[["render",l]]);export{b as __pageData,y as default};
