import{_ as t,o,c as r,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 22.1-111 Trust funds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-111.md","filePath":"vacode/22.1-111.md"}'),s={name:"vacode/22.1-111.md"},n=e("h1",{id:"§-22-1-111-trust-funds",tabindex:"-1"},[a("§ 22.1-111 Trust funds. "),e("a",{class:"header-anchor",href:"#§-22-1-111-trust-funds","aria-label":'Permalink to "§ 22.1-111 Trust funds."'},"​")],-1),i=e("p",null,"In cases where funds or other property are held by trustees for purposes of public school education, a school board shall have the power and duty to examine into the manner in which such trusts are administered. All such trustees shall render reports to the school board whenever called on and afford every facility needed by the school board to obtain a full understanding of all the points connected with such administration. If such examination reveals any defect or irregularity in the administration of such trust funds or other property, it shall be the duty of the school board to institute prompt proceedings for carrying the matter before the courts. In cases where donations or other funds have been set apart for the education of the poor, a school board is authorized to receive and apply the same in connection with the public schools in obedience to the will of the donor. Nothing in this section shall be construed to apply to the twenty-fifth clause of the will of Samuel Miller, deceased, or in anywise to affect or impair any rights or interests whatsoever, either public or private, arising under such clause.",-1),h=e("p",null,"Code 1950, § 22-144; 1980, c. 559.",-1),c=[n,i,h];function d(l,u,f,p,m,_){return o(),r("div",null,c)}const v=t(s,[["render",d]]);export{y as __pageData,v as default};
