import{_ as a,o,c as t,z as e,a as s}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 22.1-117 Fiscal agents of certain school divisions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-117.md","filePath":"vacode/22.1-117.md"}'),n={name:"vacode/22.1-117.md"},i=e("h1",{id:"§-22-1-117-fiscal-agents-of-certain-school-divisions",tabindex:"-1"},[s("§ 22.1-117 Fiscal agents of certain school divisions. "),e("a",{class:"header-anchor",href:"#§-22-1-117-fiscal-agents-of-certain-school-divisions","aria-label":'Permalink to "§ 22.1-117 Fiscal agents of certain school divisions."'},"​")],-1),r=e("p",null,"The fiscal agent for the school board of a school division composed of part or all of more than one county or city shall be the treasurer of one of the participating counties or cities, as agreed upon by the division school board and the governing bodies. In the event agreement on the selection of a fiscal agent cannot be reached, the Board of Education shall designate such fiscal agent. For his services as fiscal agent, the treasurer shall be paid such salary as the school board and treasurer may agree upon. In the event the school board and the treasurer so designated cannot agree on such compensation, the amount of salary to be paid shall be determined by a court of competent jurisdiction. The amount so fixed by the court shall be binding upon both the treasurer and the school board. Nothing contained in this section shall affect the regular salary or expense allowance of the treasurer as fixed annually by the State Compensation Board.",-1),c=e("p",null,"Code 1950, § 22-100.10; 1954, c. 391; 1980, c. 559.",-1),h=[i,r,c];function l(d,f,u,p,g,_){return o(),t("div",null,h)}const v=a(n,[["render",l]]);export{m as __pageData,v as default};
