import{_ as t,o,c as s,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 22.1-121 Proceedings against officers, etc., to compel settlement of accounts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-121.md","filePath":"vacode/22.1-121.md"}'),r={name:"vacode/22.1-121.md"},n=e("h1",{id:"§-22-1-121-proceedings-against-officers-etc-to-compel-settlement-of-accounts",tabindex:"-1"},[a("§ 22.1-121 Proceedings against officers, etc., to compel settlement of accounts. "),e("a",{class:"header-anchor",href:"#§-22-1-121-proceedings-against-officers-etc-to-compel-settlement-of-accounts","aria-label":'Permalink to "§ 22.1-121 Proceedings against officers, etc., to compel settlement of accounts."'},"​")],-1),c=e("p",null,"The school board shall have the power and duty, in the event of any delinquency or any irregularity in the acts of any treasurer, officer, agent or employee handling school funds or of any member of such school board, to take such steps and institute such legal proceedings as may be necessary and proper in order to secure complete settlement of the accounts of such treasurer, officer, agent, employee or member of such school board and a full and clear exhibit of the transactions of such treasurer, officer, agent, employee or member of such school board in connection with the receipts and disbursements of any funds for public school purposes and to compel the payment of any balances that may be in the hands of such treasurer or other person. The school board shall have the power and duty to take such steps and institute such legal proceedings as may be necessary and proper to secure a complete settlement of the accounts of any trustees to whom any funds or other property for the purposes of public school education shall have been entrusted and to secure a full and proper administration of such trusts; and to this end it may institute suit for the removal, for good cause shown, of such trustee or trustees and for the appointment of new trustees either to replace those removed or to fill vacancies and may institute such suits or actions as may be necessary to compel the payment of any balances in the hands of the old trustee or trustees so removed or to correct any defect or irregularity whatever in the administration of such trust fund or other property.",-1),i=e("p",null,"Code 1950, § 22-136; 1980, c. 559.",-1),h=[n,c,i];function d(l,u,f,p,m,y){return o(),s("div",null,h)}const _=t(r,[["render",d]]);export{b as __pageData,_ as default};
