import{_ as t,o as a,c as s,z as e,a as o}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 22.1-123 Petty cash funds; payment of claims from petty cash.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-123.md","filePath":"vacode/22.1-123.md"}'),n={name:"vacode/22.1-123.md"},r=e("h1",{id:"§-22-1-123-petty-cash-funds-payment-of-claims-from-petty-cash",tabindex:"-1"},[o("§ 22.1-123 Petty cash funds; payment of claims from petty cash. "),e("a",{class:"header-anchor",href:"#§-22-1-123-petty-cash-funds-payment-of-claims-from-petty-cash","aria-label":'Permalink to "§ 22.1-123 Petty cash funds; payment of claims from petty cash."'},"​")],-1),i=e("p",null,"Any school board may by resolution establish one or more petty cash funds, not exceeding $2,000 each, for the payment of claims arising from commitments made pursuant to provisions of law.",-1),c=e("p",null,"A school board may appoint an agent or other person who shall be authorized only to approve payment of claims arising from commitments made pursuant to provisions of law from such petty cash funds as may be established by the school board. Any agent or person into whose hands any such fund is placed may pay such claims therefrom without necessity of prior receipt and audit of the claims by the school board and without approval and issuance of the warrant of the school board.",-1),h=e("p",null,"The clerk of the school board shall report this action to the school board or to any appointed agent of the school board for approval and reimbursement at least within thirty days of the month following the month in which any claim has been paid.",-1),d=e("p",null,"Any agent or person into whose hands such fund is placed shall give bond with surety in the amount of $4,000, provided that additional bond shall not be required of any agent or person already bonded in the required amount.",-1),l=e("p",null,[o("Code 1950, § 22-74; 1972, c. 462; 1976, c. 18; 1980, c. 559; 1985, c. 194; 1991, c. 386; 1994, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0055",target:"_blank",rel:"noreferrer"},"55"),o(".")],-1),p=[r,i,c,h,d,l];function m(f,u,y,_,b,g){return a(),s("div",null,p)}const x=t(n,[["render",m]]);export{w as __pageData,x as default};
