import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 22.1-142 How Fund constituted; management.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-142.md","filePath":"vacode/22.1-142.md"}'),r={name:"vacode/22.1-142.md"},o=n('<h1 id="§-22-1-142-how-fund-constituted-management" tabindex="-1">§ 22.1-142 How Fund constituted; management. <a class="header-anchor" href="#§-22-1-142-how-fund-constituted-management" aria-label="Permalink to &quot;§ 22.1-142 How Fund constituted; management.&quot;">​</a></h1><p>There shall be set apart as a permanent and perpetual fund, to be known as the &quot;Literary Fund,&quot; the present Literary Fund of the Commonwealth, donations to the Literary Fund, sums appropriated to the Literary Fund, all funds received by the State Treasurer and required to be deposited in the Literary Fund pursuant to Chapter 25 (§ <a href="/vacode/55.1-2500/">55.1-2500</a> et seq.) of Title 55.1 and the proceeds of (i) all public lands donated by Congress for public school purposes, (ii) all escheated property, (iii) all waste and unappropriated lands, (iv) all property accruing to the Commonwealth by forfeiture except those items specifically exempted, (v) all fines collected for offenses committed against the Commonwealth, and (vi) the annual interest on the Literary Fund. The Literary Fund shall be invested and managed by the Board of Education as prescribed by § <a href="/vacode/22.1-145/">22.1-145</a>.</p><p>Code 1950, § 22-101; 1971, Ex. Sess., c. 128; 1980, c. 559; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',3),i=[o];function d(s,l,c,p,u,h){return t(),a("div",null,i)}const _=e(r,[["render",d]]);export{m as __pageData,_ as default};
