import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 22.1-146.1 School modernization loan interest rate subsidy payments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-146.1.md","filePath":"vacode/22.1-146.1.md"}'),i={name:"vacode/22.1-146.1.md"},n=a('<h1 id="§-22-1-146-1-school-modernization-loan-interest-rate-subsidy-payments" tabindex="-1">§ 22.1-146.1 School modernization loan interest rate subsidy payments. <a class="header-anchor" href="#§-22-1-146-1-school-modernization-loan-interest-rate-subsidy-payments" aria-label="Permalink to &quot;§ 22.1-146.1 School modernization loan interest rate subsidy payments.&quot;">​</a></h1><p>A. The Board of Education shall establish a program to subsidize interest payments on certain loans made by the Virginia Public School Authority to local governing bodies and school boards for the design and construction of new school buildings and facilities or the modernization and maintenance of existing school buildings and facilities as follows:</p><ol><li><p>For school divisions with a composite index of local ability-to-pay that is greater than 0.2500 but less than 0.4000 at the time an application to the program is made, the Board may subsidize up to 50 percent of the interest due on such loan; or</p></li><li><p>For school divisions with a composite index of local ability-to-pay that is 0.2500 or less at the time an application to the program is made, the Board may subsidize up to 100 percent of the interest rate on such loan.</p></li></ol><p>B. The aggregate amount of subsidies provided pursuant to this section shall not exceed the allocation specified in the general appropriation act for each fiscal year.</p><p>C. For each loan, the annual subsidy amount shall not include both:</p><ol><li><p>Interest or other loan-related costs related to any part of the loan that exceed $7.5 million in loan value; and</p></li><li><p>Interest on any part of the loan that exceeds the rate that the local governing body or school board would have obtained for such loan under the provisions of §§ <a href="/vacode/22.1-146/">22.1-146</a> and <a href="/vacode/22.1-150/">22.1-150</a>.</p></li></ol><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>.</p>',7),s=[n];function r(l,d,c,h,p,u){return o(),t("div",null,s)}const _=e(i,[["render",r]]);export{f as __pageData,_ as default};
