import{_ as t,o as n,c as r,z as e,a as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-158 Provisions for payment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-158.md","filePath":"vacode/22.1-158.md"}'),a={name:"vacode/22.1-158.md"},i=e("h1",{id:"§-22-1-158-provisions-for-payment",tabindex:"-1"},[o("§ 22.1-158 Provisions for payment. "),e("a",{class:"header-anchor",href:"#§-22-1-158-provisions-for-payment","aria-label":'Permalink to "§ 22.1-158 Provisions for payment."'},"​")],-1),s=e("p",null,"The governing body of any county, city or town, if the town constitutes the school division, in which the school board has borrowed money from the Literary Fund shall include in its levies and appropriate to the school board a fund sufficient to meet the liabilities of the school board on such loan for the construction or renovation or enlarging of any school building, regardless of whether the title to the site is held by the school board, the local governing body or by a third party with whom the school board has entered into a long-term lease. The governing body of any county in which the school board thereof has borrowed money from the Literary Fund for construction of school facilities located in a town in such county constituting a separate school division shall have authority to include in its levies for such town, a levy sufficient to meet the liabilities of the school board on such loan and shall levy a separate tax in the rest of the county to meet its liabilities on any contract for school facilities constructed outside such town. In the event that such school board shall fail to pay any installment of interest or principal promptly, upon notice in writing to that effect from the State Treasurer, the county, city or town treasurer shall pay to the State Treasurer any such past-due installment of interest or principal, out of the funds in his hands belonging to such county, city or town. The failure of such governing body to provide for the payment of such loan or the interest thereon when and as due shall be deemed a cause for removal of the members thereof from office on motion before the circuit court having jurisdiction in such county, city or town, instituted by the attorney for the Commonwealth of such county or city or by the Attorney General where the attorney for the Commonwealth refuses or neglects to act after demand is made on him to proceed.",-1),h=e("p",null,'For the purposes of this section, "long-term lease" means a lease for a term of twenty-five years or more.',-1),c=e("p",null,[o("Code 1950, § 22-113; 1976, c. 334; 1980, c. 559; 1998, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0719",target:"_blank",rel:"noreferrer"},"719"),o(".")],-1),l=[i,s,h,c];function d(f,u,y,m,p,b){return n(),r("div",null,l)}const _=t(a,[["render",d]]);export{g as __pageData,_ as default};
