import{_ as t,o as e,c as s,z as o,a as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 22.1-159 Loans for construction of school facilities to serve portions of counties; levy of taxes for purpose of repaying such loans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-159.md","filePath":"vacode/22.1-159.md"}'),a={name:"vacode/22.1-159.md"},n=o("h1",{id:"§-22-1-159-loans-for-construction-of-school-facilities-to-serve-portions-of-counties-levy-of-taxes-for-purpose-of-repaying-such-loans",tabindex:"-1"},[i("§ 22.1-159 Loans for construction of school facilities to serve portions of counties; levy of taxes for purpose of repaying such loans. "),o("a",{class:"header-anchor",href:"#§-22-1-159-loans-for-construction-of-school-facilities-to-serve-portions-of-counties-levy-of-taxes-for-purpose-of-repaying-such-loans","aria-label":'Permalink to "§ 22.1-159 Loans for construction of school facilities to serve portions of counties; levy of taxes for purpose of repaying such loans."'},"​")],-1),r=o("p",null,"Notwithstanding any other provision of law to the contrary, the school board of any school division composed of part or all of a county, with the approval of the governing body of the county, is authorized to borrow from the Literary Fund for the purpose of constructing school facilities in such county to serve a portion of such county. Taxes on property in the magisterial districts served by such facilities shall be levied by the governing body of the county and collected for the purpose of repaying such loan; provided that, for the purposes of this section, a magisterial district shall not include a town constituting a separate school division but the governing body of the county may levy a separate tax on property in a town in such county constituting a separate school division to repay money borrowed by such county from the Literary Fund for the purpose of constructing school facilities in such town. Except as otherwise provided by this section all other provisions of law relating to Literary Fund loans shall apply to a loan authorized by this section.",-1),c=o("p",null,"Code 1950, § 22-107.3; 1962, c. 177; 1976, c. 334; 1980, c. 559.",-1),l=[n,r,c];function f(h,p,u,d,y,v){return e(),s("div",null,l)}const m=t(a,[["render",f]]);export{_ as __pageData,m as default};
