import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-16.1 Board to establish regulations regarding human research.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-16.1.md","filePath":"vacode/22.1-16.1.md"}'),o={name:"vacode/22.1-16.1.md"},i=t('<h1 id="§-22-1-16-1-board-to-establish-regulations-regarding-human-research" tabindex="-1">§ 22.1-16.1 Board to establish regulations regarding human research. <a class="header-anchor" href="#§-22-1-16-1-board-to-establish-regulations-regarding-human-research" aria-label="Permalink to &quot;§ 22.1-16.1 Board to establish regulations regarding human research.&quot;">​</a></h1><p>The Board shall promulgate regulations pursuant to the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.) to effectuate the provisions of Chapter 5.1 (§ <a href="http://law.lis.virginia.gov/vacode/32.1-162.16/" target="_blank" rel="noreferrer">32.1-162.16</a> et seq.) of Title 32.1 for human research, as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-162.16/" target="_blank" rel="noreferrer">32.1-162.16</a>, to be conducted or authorized by the Department of Education or any public schools including the Virginia Schools for the Deaf and the Blind or any proprietary schools certified by the Board. The regulations shall require the human research committee to submit to the Governor, the General Assembly, and the Superintendent of Public Instruction or his designee at least annually a report on the human research projects reviewed and approved by the committee and shall require the committee to report any significant deviations from the proposals as approved.</p><p>1992, c. 603.</p>',3),n=[i];function s(h,l,c,d,u,p){return a(),r("div",null,n)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
