import{_ as t,o as n,c as r,z as e,a as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 22.1-161.2 Resolution by school board; approval or rejection by governing body; indebtedness evidenced by bonds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-161.2.md","filePath":"vacode/22.1-161.2.md"}'),s={name:"vacode/22.1-161.2.md"},i=e("h1",{id:"§-22-1-161-2-resolution-by-school-board-approval-or-rejection-by-governing-body-indebtedness-evidenced-by-bonds",tabindex:"-1"},[o("§ 22.1-161.2 Resolution by school board; approval or rejection by governing body; indebtedness evidenced by bonds. "),e("a",{class:"header-anchor",href:"#§-22-1-161-2-resolution-by-school-board-approval-or-rejection-by-governing-body-indebtedness-evidenced-by-bonds","aria-label":'Permalink to "§ 22.1-161.2 Resolution by school board; approval or rejection by governing body; indebtedness evidenced by bonds."'},"​")],-1),a=e("p",null,"Whenever a school board desires to contract with the Board of Trustees of the Virginia Retirement System to borrow money for capital projects for school purposes, it shall adopt a resolution setting forth the purpose for which it is desired to borrow the money and the amount of such proposed borrowing. Such resolution shall be entered in the minutes of the school board, and a copy of the same, certified by the clerk of the school board, shall be submitted by the school board to the governing body of the county, city or town for its approval or rejection. If the governing body approves the resolution, it shall enter its approval in its minutes, and the school board may then endeavor to negotiate an agreement with the Board of Trustees of the Virginia Retirement System for the borrowing of such money. If agreement is reached, the question of borrowing such money on the terms agreed upon by the school board and the Board of Trustees of the Virginia Retirement System shall again be submitted to the governing body of the county, city or town for its approval or rejection. If the governing body approves the terms of the agreement, it shall enter its approval in its minutes, and the school board may then, by resolution entered in its minutes, provide for the issuance of negotiable bonds evidencing the indebtedness for sale to the Virginia Retirement System. Such bonds shall be issued in conformity with the provisions of this chapter.",-1),d=e("p",null,[o("1995, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0250",target:"_blank",rel:"noreferrer"},"250"),o(".")],-1),h=[i,a,d];function c(l,b,p,y,f,g){return n(),r("div",null,h)}const v=t(s,[["render",c]]);export{m as __pageData,v as default};
