import{_ as a,o as t,c as s,z as e,a as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-161.3 Issuance of bonds; procedure; form and requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-161.3.md","filePath":"vacode/22.1-161.3.md"}'),n={name:"vacode/22.1-161.3.md"},r=e("h1",{id:"§-22-1-161-3-issuance-of-bonds-procedure-form-and-requirements",tabindex:"-1"},[o("§ 22.1-161.3 Issuance of bonds; procedure; form and requirements. "),e("a",{class:"header-anchor",href:"#§-22-1-161-3-issuance-of-bonds-procedure-form-and-requirements","aria-label":'Permalink to "§ 22.1-161.3 Issuance of bonds; procedure; form and requirements."'},"​")],-1),h=e("p",null,"Such bonds shall be issued by the school board in the name of the county, city or town. For the payment of the principal of and the interest on such bonds, the full faith and credit of the county, city or town shall be pledged. The bonds shall be signed by the chairman of the school board and countersigned by the clerk thereof, but the bonds may bear or be executed with the facsimile signature of one of such officials, and in the case of coupon bonds, the coupons may bear the facsimile signatures of both of such officials; the bonds shall be under the seal of the school board, but in lieu of impressing such seal physically upon such bonds, a facsimile of such seal may be imprinted on the bonds if so authorized by the school board. The bonds shall be in the denomination or denominations of not less than $1,000 each; they may be in coupon or registered form, or both, as may be agreed upon by the school board and the Board of Trustees of the Virginia Retirement System; they shall bear interest at the agreed rate or rates, and such interest shall be payable semiannually; they shall be serial bonds with maturities and amounts as agreed upon but the first maturity date shall not be longer than two years from the date of such bonds and the maximum maturity date shall not be longer than thirty years from the date of such bonds. The place or places of payment of principal and interest shall be as agreed upon by the school board and the Board of Trustees of the Virginia Retirement System. In case any officer whose signature or a facsimile thereof shall appear on any bond or coupon shall cease to be such officer before the delivery of such bond, the signature or facsimile shall nevertheless be valid and sufficient for all purposes the same as if he had remained in office until such delivery, and any bond may bear the signature of a person who at the actual time of the execution of such bond shall be the proper officer to sign the bond although at the date of the bond, the person may not have been such officer.",-1),i=e("p",null,[o("1995, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0250",target:"_blank",rel:"noreferrer"},"250"),o(".")],-1),d=[r,h,i];function c(l,f,u,b,m,p){return t(),s("div",null,d)}const _=a(n,[["render",c]]);export{g as __pageData,_ as default};
